  `tel` varchar(30) DEFAULT NULL,
  `subject` varchar(100) NOT NULL,
  `message` longtext NOT NULL,
  `rel_order` bigint(20) DEFAULT '0',
  `date_line` bigint(20) NOT NULL DEFAULT '0',
